CREATE TABLE Form (\n    FormID INT PRIMARY KEY,\n    -- other columns...\n);\n\nCREATE TABLE Answer (\n    AnswerID INT PRIMARY KEY,\n    FormID INT,\n    AnswerText VARCHAR(MAX),\n    FOREIGN KEY (FormID) REFERENCES Form(FormID)\n);\n\nCREATE TABLE Comment (\n    CommentID INT PRIMARY KEY,\n    FormID INT,\n    CommentText VARCHAR(MAX),\n    FOREIGN KEY (FormID) REFERENCES Form(FormID)\n);\n\nCREATE FUNCTION GetAnswerCommentText(@FormID INT)\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @text VARCHAR(MAX);\n    SET @text = '';\n    SELECT @text += AnswerText + ' ' FROM Answer WHERE FormID = @FormID;\n    SELECT @text += CommentText + ' ' FROM Comment WHERE FormID = @FormID;\n    RETURN @text;\nEND;\n\nCREATE FULLTEXT INDEX ON Form (\n    FormID,\n    GetAnswerCommentText(FormID)\n) KEY INDEX PK_Form;