ALTER PROCEDURE [dbo].[getSealRecordDate]  (@SEAL_DATE DATETIME)\nAS\nBEGIN\n    -- Declare variables\n    DECLARE @SEAL_DATE_PLUS_ONE_SECOND DATETIME\n\n    -- Add one second to the @SEAL_DATE\n    SET @SEAL_DATE_PLUS_ONE_SECOND = DATEADD(second, 1, @SEAL_DATE)\n\n    SELECT DISTINCT \n        "FGFSTRANSFERS"."CONSIDERATION", \n        "FGFSTRANSFERS"."AMOUNT_TRANSFER", \n        "FGFSTRANSFERS"."DATE", \n        "FGFSTransferDetails"."TRANSFER_ID", \n        "FGFSTRANSFERS"."TRANSFER_ID", \n        "FGFSTRANSFERS"."Curr", \n        "FGFSTransferDetails"."CURR", \n        "FGFSCUSTOMERS"."CMF_NAME_1", \n        "FGFSCUSTOMERS"."CMF_NAME_2"\n    FROM   \n        ("FGFSInvestment"."dbo"."FGFSTransferDetails" "FGFSTransferDetails" \n        INNER JOIN "FGFSInvestment"."dbo"."FGFSTRANSFERS" "FGFSTRANSFERS" \n        ON "FGFSTransferDetails"."TRANSFER_ID"="FGFSTRANSFERS"."TRANSFER_ID") \n        INNER JOIN "FGFSInvestment"."dbo"."FGFSCUSTOMERS" "FGFSCUSTOMERS" \n        ON "FGFSTRANSFERS"."CUSTODIAN"="FGFSCUSTOMERS"."CMF_ACCOUNT"\n    WHERE  \n        ("FGFSTRANSFERS"."DATE">= @SEAL_DATE AND "FGFSTRANSFERS"."DATE"< @SEAL_DATE_PLUS_ONE_SECOND)\nEND