CREATE PROCEDURE sp_InsertIntoTables\n    @Table1Columns -- other parameters\n    @Table2Columns -- other parameters\nAS\nBEGIN\n    DECLARE @SequenceID INT;\n    UPDATE SequenceTable SET SequenceID = SequenceID + 1;\n    SET @SequenceID = (SELECT SequenceID FROM SequenceTable);\n    INSERT INTO Table1 (ID, -- other columns)\n    VALUES (@SequenceID, -- other values);\n    INSERT INTO Table2 (ID, -- other columns)\n    VALUES (@SequenceID + 1, -- other values);\nEND;