CREATE TABLE SequenceTable (\n    SequenceID INT IDENTITY(1,1) PRIMARY KEY\n);\n\nCREATE TABLE Table1 (\n    ID INT PRIMARY KEY,\n    -- other columns\n);\n\nCREATE TABLE Table2 (\n    ID INT PRIMARY KEY,\n    -- other columns\n);\n\nCREATE TRIGGER trg_Table1_Insert ON Table1\nINSTEAD OF INSERT\nAS\nBEGIN\n    DECLARE @SequenceID INT;\n    UPDATE SequenceTable SET SequenceID = SequenceID + 1;\n    SET @SequenceID = (SELECT SequenceID FROM SequenceTable);\n    INSERT INTO Table1 (ID, -- other columns)\n    VALUES (@SequenceID, -- other values);\nEND;\n\nCREATE TRIGGER trg_Table2_Insert ON Table2\nINSTEAD OF INSERT\nAS\nBEGIN\n    DECLARE @SequenceID INT;\n    UPDATE SequenceTable SET SequenceID = SequenceID + 1;\n    SET @SequenceID = (SELECT SequenceID FROM SequenceTable);\n    INSERT INTO Table2 (ID, -- other columns)\n    VALUES (@SequenceID, -- other values);\nEND;