CREATE PROCEDURE MyProcedure\n    @itemNumber INT\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX)\n\n    IF @itemType = 'A'\n        SET @sql = 'SELECT ItemNumber, ItemType, Description FROM TableA WHERE ItemNumber = @itemNumber'\n    ELSE\n        SET @sql = 'SELECT ItemNumber, ItemType, Description FROM TableB WHERE PartNumber = @itemNumber'\n\n    EXEC sp_executesql @sql, N'@itemNumber INT', @itemNumber = @itemNumber\nEND