SELECT \n  (SELECT SUM(bytes) FROM V$SGASTAT WHERE name = 'free memory') AS free_memory,\n  (SELECT SUM(bytes) FROM V$SGASTAT WHERE name = 'shared pool') AS shared_pool,\n  (SELECT SUM(bytes) FROM V$SGASTAT WHERE name = 'large pool') AS large_pool,\n  (SELECT SUM(bytes) FROM V$SGASTAT WHERE name = 'java pool') AS java_pool,\n  (SELECT SUM(bytes) FROM V$SGASTAT WHERE name = 'streams pool') AS streams_pool\nFROM DUAL;