// Get list of tables\nSELECT NAME FROM MSysObjects WHERE Type = 1 AND Flags = 0\n\n// Get list of columns for a specific table\nSELECT * FROM MSysColumns WHERE TblID = (SELECT ID FROM MSysObjects WHERE Name = 'YourTableName')\n\n// Get list of indexes for a specific table\nSELECT * FROM MSysIndexes WHERE TblID = (SELECT ID FROM MSysObjects WHERE Name = 'YourTableName')\n\n// Get list of relationships\nSELECT * FROM MSysRelationships