select u.fname, u.lname, u.role, u.username, \n       r.building, r.room, r.region, \n       null as office, null as office_num\nfrom users u\njoin ra_ca r on (u.username = r.username)\nwhere u.username = 'behrk2' and r.region = '4'\n\nunion all\n\nselect u.fname, u.lname, u.role, u.username, \n       null as building, null as room, null as region, \n       a.office, a.office_num\nfrom users u\njoin area_coord a on (u.username = a.username)\nwhere u.username = 'behrk2' and a.region = '4';