DECLARE @sql NVARCHAR(MAX) = ''\n\nSELECT @sql += 'DROP INDEX ' + i.name + ' ON ' + t.name + '; '\nFROM sys.indexes i\nINNER JOIN sys.tables t ON i.object_id = t.object_id\nINNER JOIN sys.index_columns ic ON i.object_id = ic.object_id AND i.index_id = ic.index_id\nINNER JOIN sys.columns c ON ic.object_id = c.object_id AND ic.column_id = c.column_id\nWHERE c.system_type_id IN (231, 167, 239, 231, 98)  -- string data types\nAND t.name = 'YourTableName'  -- replace with your table name\nAND c.name = 'YourColumnName'  -- replace with your column name\n\nEXEC sp_executesql @sql