WITH OriginalQuery AS (\n  SELECT \n    Marketer, \n    SubMarketer, \n    Grade, \n    Total, \n    Convert(varchar,Date,101) AS [Date]\n  FROM \n    SomeTable\n  WHERE \n    Date >= '2/25/2009' AND Date < '2/26/2009'\n    AND Marketer IN ('22','500')\n  GROUP BY \n    SubMarketer, Grade, Marketer, Date, Total\n)\nSELECT \n  Marketer, \n  SubMarketer, \n  Grade, \n  Total, \n  [Date]\nFROM \n  OriginalQuery\nUNION ALL\nSELECT \n  Marketer, \n  'Marketer Total', \n  NULL, \n  SUM(Total), \n  NULL\nFROM \n  OriginalQuery\nGROUP BY \n  Marketer\nORDER BY \n  Marketer, \n  CASE WHEN SubMarketer = 'Marketer Total' THEN 1 ELSE 0 END