-- Create publication on primary database\nCREATE PUBLICATION AspStateReplication\n    FOR DATABASE = 'YourPrimaryDatabase'\n    WITH REPLICATION = 'Bidirectional';\n\n-- Add tables to publication\nALTER PUBLICATION AspStateReplication\n    ADD TABLE ASPStateTempSessions;\nALTER PUBLICATION AspStateReplication\n    ADD TABLE ASPStateTempApplications;\nALTER PUBLICATION AspStateReplication\n    ADD TABLE Membership;\nALTER PUBLICATION AspStateReplication\n    ADD TABLE Roles;\nALTER PUBLICATION AspStateReplication\n    ADD TABLE Personalization;\n\n-- Create subscription on secondary database\nCREATE SUBSCRIPTION AspStateReplication\n    FOR PUBLICATION = 'AspStateReplication'\n    WITH DATABASE = 'YourSecondaryDatabase';\n\n-- Configure replication agents\nEXEC sp_addpullsubscription_agent \n    @publisher = 'YourPrimaryDatabase', \n    @publisher_db = 'YourPrimaryDatabase', \n    @publication = 'AspStateReplication', \n    @subscriber = 'YourSecondaryDatabase', \n    @subscriber_db = 'YourSecondaryDatabase';