CREATE FUNCTION MyFunction \n(\n    @param sql_variant\n)\nRETURNS sql_variant\nAS\nBEGIN\n    IF TRY_CAST(@param AS int) IS NOT NULL\n        -- Do something with @param as int\n        RETURN TRY_CAST(@param AS int) * 2;\n    ELSE IF TRY_CAST(@param AS varchar) IS NOT NULL\n        -- Do something with @param as varchar\n        RETURN TRY_CAST(@param AS varchar) + ' world';\n    ELSE\n        RAISERROR('Invalid parameter type', 16, 1);\nEND\nGO