CREATE EVENT SESSION [Capture Queries] ON SERVER \nADD EVENT sqlserver.sql_batch_completed(\n    ACTION(sqlserver.client_app_name, sqlserver.database_name, sqlserver.query_hash)\n    WHERE sqlserver.client_app_name = 'YourApplicationName'\n) WITH (MAX_MEMORY = 4096 KB, EVENT_RETENTION_MODE = ALLOW_SINGLE_EVENT_LOSS, MAX_DISPATCH_LATENCY = 30 SECONDS, MAX_EVENT_SIZE = 0 KB, MEMORY_PARTITION_MODE = NONE, TRACK_CAUSALITY = OFF, STARTUP_STATE = ON);\nALTER EVENT SESSION [Capture Queries] ON SERVER STATE = START;