WITH RecursiveCustomers AS (\n    SELECT CustomerID, ParentID, Name, 0 AS Level\n    FROM Customers\n    WHERE Name = 'James'  -- Anchor query, start with James\n\n    UNION ALL\n\n    SELECT c.CustomerID, c.ParentID, c.Name, Level + 1\n    FROM Customers c\n    INNER JOIN RecursiveCustomers rc ON c.ParentID = rc.CustomerID  -- Recursive join\n)\nSELECT Name\nFROM RecursiveCustomers\nWHERE Level > 0;  -- Get descendants, exclude James himself