SELECT \n    COALESCE(c.ContactDate, 'No Entry') AS ContactDate,\n    COALESCE(c.SortName, 'No Entry') AS SortName,\n    COALESCE(a.City, 'No Entry') AS City,\n    COALESCE(c.ContactType, 'No Entry') AS ContactType,\n    COALESCE(c.Summary, 'No Entry') AS Summary\nFROM \n    ContactTable c\nJOIN \n    UserTable u ON c.UserID = u.UserID\nJOIN \n    AddressTable a ON u.AddressID = a.AddressID;