DECLARE\n  ROLE_EXISTS NUMBER;\nBEGIN\n  SELECT COUNT(*) INTO ROLE_EXISTS\n  FROM DBA_ROLES\n  WHERE ROLE = 'ROLE_NAME';\n\n  IF ROLE_EXISTS = 0 THEN\n    EXECUTE IMMEDIATE 'CREATE ROLE ROLE_NAME';\n  ELSE\n    EXECUTE IMMEDIATE 'DROP ROLE ROLE_NAME';\n    EXECUTE IMMEDIATE 'CREATE ROLE ROLE_NAME';\n  END IF;\nEND;\n/\nGRANT SELECT ON SCM1_VIEW_OBJECT_VW TO role_name;