DECLARE @StartDate DATE = '2023-05-11';\nDECLARE @EndDate DATE = '2023-05-17';\nDECLARE @fID VARCHAR(10) = 'P1';\n\nWITH DateRange AS (\n  SELECT @StartDate AS Date\n  UNION ALL\n  SELECT DATEADD(DAY, 1, Date)\n  FROM DateRange\n  WHERE Date < @EndDate\n)\nSELECT \n  dr.Date,\n  COALESCE(t.bID, '--') AS bID,\n  COALESCE(t.sName, '--') AS sName\nFROM DateRange dr\nLEFT JOIN table1 t ON dr.Date = t.date AND t.fID = @fID\nORDER BY dr.Date;