UPDATE os\nSET os.oildatasetstatusid = \n    CASE \n        WHEN os.oildatasetstatusid = 5 THEN 16\n        WHEN os.oildatasetstatusid = 6 THEN 17\n        WHEN os.oildatasetstatusid = 7 THEN 18\n        WHEN os.oildatasetstatusid = 8 THEN 18\n        WHEN os.oildatasetstatusid = 9 THEN 18\n        WHEN os.oildatasetstatusid = 10 THEN 19\n        WHEN os.oildatasetstatusid = 11 THEN 20\n    END\nFROM oildatasetstatus os\nINNER JOIN oildataset od ON os.oildatasetid = od.oildatasetid\nINNER JOIN samplepoint sp ON od.samplepointid = sp.samplepointid\nINNER JOIN customersite cs ON sp.customersiteid = cs.customersiteid\nWHERE cs.customerid = 2;\n\nUPDATE od\nSET od.oillabid = 9\nFROM oildataset od\nINNER JOIN oildatasetstatus os ON od.oildatasetid = os.oildatasetid\nWHERE os.oildatasetstatusid BETWEEN 16 AND 20;