CREATE FUNCTION dbo.RemoveNonAlphanumeric (@input VARCHAR(MAX))\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @output VARCHAR(MAX) = ''\n    DECLARE @char CHAR(1)\n    DECLARE @index INT = 1\n\n    WHILE @index <= LEN(@input)\n    BEGIN\n        SET @char = SUBSTRING(@input, @index, 1)\n        IF @char LIKE '[a-zA-Z0-9]'\n            SET @output += @char\n        SET @index += 1\n    END\n\n    RETURN @output\nEND