CREATE TABLE expenses (\n  id INT PRIMARY KEY,\n  user_id INT,\n  dated_on DATE,\n  INDEX idx_user_id_dated_on (user_id, dated_on)\n);\n\n-- Query 1: uses the multi-column index efficiently\nSELECT * FROM expenses WHERE user_id = 1;\n\n-- Query 2: cannot use the multi-column index efficiently\nSELECT * FROM expenses WHERE dated_on = '2022-01-01';\n\n-- Create a separate index on dated_on\nCREATE INDEX idx_dated_on ON expenses (dated_on);\n\n-- Query 3: can use the new index on dated_on\nSELECT * FROM expenses WHERE dated_on = '2022-01-01';