SELECT \n  p.Name, \n  c.ClubName, \n  r.Start_date, \n  r.End_date\nFROM \n  Registrations r\n  JOIN Teams t ON r.TeamID = t.ID\n  JOIN Clubs c ON t.ClubID = c.ID\n  JOIN Players p ON r.PlayerID = p.ID\nWHERE \n  r.SeasonID = 2008\n  AND p.ID IN (\n    SELECT \n      r2.PlayerID\n    FROM \n      Registrations r2\n      JOIN Teams t2 ON r2.TeamID = t2.ID\n    WHERE \n      r2.SeasonID = 2008\n    GROUP BY \n      r2.PlayerID\n    HAVING \n      COUNT(DISTINCT t2.ClubID) > 1\n  )