SELECT p.*\nFROM weblPlayers p\nJOIN (\n  SELECT winner AS player_name, COUNT(*) AS games_played\n  FROM weblGames\n  WHERE DATE(game_date) >= DATE_SUB(CURDATE(), INTERVAL y DAY)\n  GROUP BY winner\n  HAVING COUNT(*) >= x\n  UNION ALL\n  SELECT loser AS player_name, COUNT(*) AS games_played\n  FROM weblGames\n  WHERE DATE(game_date) >= DATE_SUB(CURDATE(), INTERVAL y DAY)\n  GROUP BY loser\n  HAVING COUNT(*) >= x\n) g ON p.name = g.player_name