SELECT \n  i.itemName,\n  COALESCE(SUM(v.value), 0) AS value,\n  m.month\nFROM \n  t_item i\n  CROSS JOIN (\n    SELECT 1 AS month UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 UNION SELECT 5 UNION SELECT 6 UNION SELECT 7 UNION SELECT 8 UNION SELECT 9 UNION SELECT 10 UNION SELECT 11 UNION SELECT 12\n  ) m\n  LEFT JOIN t_value v ON i.itemID = v.itemID AND MONTH(v.date) = m.month\nGROUP BY \n  i.itemName, m.month\nORDER BY \n  i.itemName, m.month