CREATE PROCEDURE UpdateBug\n    @intID int,\n    @strTitre nvarchar(50),\n    @strDescription nvarchar(200),\n    @intStatus_FK int,\n    @intType_FK int,\n    @intSeriousness_FK int\nAS\nBEGIN\n    UPDATE [tbl_Bug] \n    SET [strTitre] = @strTitre,\n        [strDescription] = @strDescription,\n        [intStatus_FK] = @intStatus_FK,\n        [intType_FK] = @intType_FK,\n        [intSeriousness_FK] = @intSeriousness_FK\n    WHERE [intID] = @intID\nEND