CREATE TABLE Clients (\n    Id UNIQUEIDENTIFIER PRIMARY KEY DEFAULT (NEWSEQUENTIALID()),\n    AdminUserId UNIQUEIDENTIFIER NULL,\n    -- ...\n);\n\n-- Create a special "none selected" GUID value\nDECLARE @NoneSelected UNIQUEIDENTIFIER = '00000000-0000-0000-0000-000000000000';\n\n-- Use the special value when inserting a new client without an admin user\nINSERT INTO Clients (AdminUserId) VALUES (@NoneSelected);