-- Add a version column to your table\nALTER TABLE your_table ADD COLUMN version INT;\n\n-- Read the current version\nSELECT version INTO @version FROM your_table WHERE memberid = $memberid AND classid = $classid;\n\n-- Update the booking status with a version check\nUPDATE your_table SET book = TRUE, version = @version + 1\nWHERE memberid = $memberid AND classid = $classid AND version = @version;\n\n-- If the update fails, retry or abort\nIF ROW_COUNT() = 0 THEN\n    -- Retry or abort\nEND IF;