SELECT \n    T.TABNAME AS TABLE_NAME,\n    C.CONSTNAME AS CONSTRAINT_NAME\nFROM \n    SYS.SYSCONSTRAINTS C\nINNER JOIN \n    SYS.SYSTABLES T ON C.TABLEID = T.TABLEID\nWHERE \n    T.SCHEMAID = (SELECT SCHEMAID FROM SYS.SYSSCHEMAS WHERE SCHEMANAME = 'your_schema_name')\nORDER BY \n    T.TABNAME, C.CONSTNAME;