SELECT WebStoreID, webordernumber\nFROM (\n  SELECT WebStoreID, min(webordernumber) AS min_order, max(webordernumber) AS max_order\n  FROM orders\n  WHERE ordertype = 'WEB'\n  GROUP BY WebStoreID\n) AS store_orders\nJOIN LATERAL (\n  SELECT generate_series(min_order, max_order) AS webordernumber\n) AS order_numbers ON true\nWHERE NOT EXISTS (\n  SELECT 1\n  FROM orders\n  WHERE WebStoreID = store_orders.WebStoreID\n    AND webordernumber = order_numbers.webordernumber\n    AND ordertype = 'WEB'\n)