CREATE TABLE Floors (\n  FloorID INT PRIMARY KEY,\n  Name VARCHAR(50)\n)\n\nCREATE TABLE Rows (\n  RowID INT PRIMARY KEY,\n  FloorID INT FOREIGN KEY REFERENCES Floors(FloorID),\n  Name VARCHAR(50)\n)\n\nCREATE TABLE Columns (\n  ColumnID INT PRIMARY KEY,\n  RowID INT FOREIGN KEY REFERENCES Rows(RowID),\n  Name VARCHAR(50)\n)\n\nCREATE TABLE Shelves (\n  ShelfID INT PRIMARY KEY,\n  ColumnID INT FOREIGN KEY REFERENCES Columns(ColumnID),\n  Name VARCHAR(50)\n)\n\nCREATE TABLE BoxSlots (\n  BoxSlotID INT PRIMARY KEY,\n  ShelfID INT FOREIGN KEY REFERENCES Shelves(ShelfID),\n  BoxCount INT\n)\n\nCREATE TABLE Boxes (\n  BoxID INT PRIMARY KEY,\n  BoxSlotID INT FOREIGN KEY REFERENCES BoxSlots(BoxSlotID),\n  Label VARCHAR(50),\n  Scanned BIT,\n  ScanDate SMALLDATETIME\n)