PREPARE stmt FROM '\nINSERT INTO site_support_docs\n      (\n          ASSET_ID,\n          TIME_STAMP,\n          SITE_NAME,\n          DOCUMENT_NAME,\n          DOCUMENT_LOCATION,\n          DOCUMENT_CONTENT,\n          DOCUMENT_LAST_MODIFIED\n      )\nVALUES (:asset_id, :time_stamp, :site_name, :document_name, :document_location, :document_content, STR_TO_DATE(:document_last_modified, ''%M %e, %Y %r''))\nON DUPLICATE KEY UPDATE asset_id = :asset_id,\n                        time_stamp = :time_stamp,\n                        site_name = :site_name,\n                        document_name = :document_name,\n                        document_location = :document_location,\n                        document_content = :document_content,\n                        document_last_modified = STR_TO_DATE(:document_last_modified, ''%M %e, %Y %r'')\n';\n\nSET @asset_id = ?;\nSET @time_stamp = ?;\nSET @site_name = ?;\nSET @document_name = ?;\nSET @document_location = ?;\nSET @document_content = ?;\nSET @document_last_modified = ?;\n\nEXECUTE stmt USING @asset_id, @time_stamp, @site_name, @document_name, @document_location, @document_content, @document_last_modified;