CREATE TABLE movies (\n  id INT PRIMARY KEY,\n  title VARCHAR(255),\n  release_date DATE,\n  -- other basic info\n);\n\nCREATE TABLE genres (\n  id INT PRIMARY KEY,\n  genre VARCHAR(50)\n);\n\nCREATE TABLE genres2movies (\n  movieID INT,\n  genreID INT,\n  PRIMARY KEY (movieID, genreID),\n  FOREIGN KEY (movieID) REFERENCES movies(id),\n  FOREIGN KEY (genreID) REFERENCES genres(id)\n);\n\nCREATE INDEX idx_genres2movies_movieID ON genres2movies(movieID);\nCREATE INDEX idx_genres2movies_genreID ON genres2movies(genreID);\n\nCREATE TABLE movie_details (\n  id INT PRIMARY KEY,\n  movieID INT,\n  plot_summary TEXT,\n  rating DECIMAL(3,2),\n  release_date DATE,\n  -- other details\n  FOREIGN KEY (movieID) REFERENCES movies(id)\n);