CREATE FUNCTION dbo.DefaultActivity\nRETURNS \n    @returnTable TABLE (\n        [VisitingCount] int,\n        [Time] nvarchar(50)\n    )\nAS\nBEGIN\n    WITH Numbers AS (\n        SELECT 0 AS Number\n        UNION ALL\n        SELECT Number + 1\n        FROM Numbers\n        WHERE Number < 1439\n    )\n    INSERT INTO @returnTable ([VisitingCount], [Time])\n    SELECT 0, RIGHT(LEFT(CONVERT(nvarchar, DATEADD(minute, Number, '00:00'), 121), 16), 5)\n    FROM Numbers\n    OPTION (MAXRECURSION 0);\n\n    RETURN;\nEND;