CREATE TABLE PARTS (PTNO NUMBER, CATCD NUMBER);\nCREATE TABLE CATEGORIES(CATCD NUMBER);\n\nINSERT INTO PARTS VALUES (1, 1);\nINSERT INTO PARTS VALUES (2, NULL);\nINSERT INTO CATEGORIES VALUES (1);\n\nSELECT PTNO, CATCD  -- ambiguous column\nFROM PARTS\nLEFT JOIN CATEGORIES ON (CATEGORIES.CATCD = PARTS.CATCD);\n\n-- returns:\n-- PTNO  CATCD\n-- 1     1\n-- 2     NULL\n\nSELECT PTNO, PARTS.CATCD  -- fully qualified column\nFROM PARTS\nLEFT JOIN CATEGORIES ON (CATEGORIES.CATCD = PARTS.CATCD);\n\n-- returns:\n-- PTNO  CATCD\n-- 1     1\n-- 2     NULL (from PARTS table)