DECLARE @schema_name sysname;\nDECLARE @table_name sysname;\n\nSET @schema_name = 'something here';\n\nSELECT \n    u.name,\n    o.name,\n    CASE (o.type) WHEN 'S' THEN 'SYSTEM TABLE' ELSE 'TABLE' END,\n    (SELECT CONVERT(VARCHAR(8000), value) FROM ::fn_listextendedproperty(NULL, 'user', @schema_name, 'table', o.name, null, null) WHERE name = 'MS_Description') AS coln\nFROM \n    sys.sysobjects o\n    JOIN sys.schemas u ON (u.schema_id = o.uid)\nWHERE \n    o.type IN ('U', 'S')\n    AND u.name = @schema_name\nORDER BY 1, 2;