CREATE TABLE answers (\n    id SERIAL PRIMARY KEY,\n    question_id INTEGER NOT NULL,\n    answer TEXT NOT NULL,\n    FOREIGN KEY (question_id) REFERENCES questions(id)\n);\n\nCREATE TABLE questions (\n    id SERIAL PRIMARY KEY,\n    user_id INTEGER NOT NULL,\n    title VARCHAR(60) NOT NULL,\n    body TEXT NOT NULL,\n    moderator_removal BOOLEAN NOT NULL DEFAULT FALSE,\n    sent_time TIMESTAMP NOT NULL,\n    FOREIGN KEY (user_id) REFERENCES user_infos(id)\n);\n\nCREATE TABLE tags (\n    id SERIAL PRIMARY KEY,\n    question_id INTEGER NOT NULL,\n    tag VARCHAR(20) NOT NULL,\n    CONSTRAINT no_duplicate_tag UNIQUE (question_id, tag),\n    FOREIGN KEY (question_id) REFERENCES questions(id)\n);\n\nCREATE TABLE user_infos (\n    id SERIAL PRIMARY KEY,\n    username VARCHAR(25),\n    email VARCHAR(320) NOT NULL UNIQUE,\n    password_sha512 VARCHAR(128) NOT NULL,\n    is_moderator BOOLEAN NOT NULL DEFAULT FALSE,\n    is_login BOOLEAN NOT NULL DEFAULT FALSE,\n    has_been_sent_a_moderator_message BOOLEAN NOT NULL DEFAULT FALSE\n);\n\nCREATE INDEX idx_questions_user_id ON questions(user_id);\nCREATE INDEX idx_tags_question_id ON tags(question_id);