SELECT * FROM (\n  SELECT \n    a.pubid,\n    a.keywords,\n    'article' AS type,\n    a.article_specific_column\n  FROM \n    articles a\n    INNER JOIN publications p ON a.pubid = p.pubid\n  WHERE \n    p.keywords LIKE '%search_keyword%'\n  UNION ALL\n  SELECT \n    b.pubid,\n    b.keywords,\n    'book' AS type,\n    b.book_specific_column\n  FROM \n    books b\n    INNER JOIN publications p ON b.pubid = p.pubid\n  WHERE \n    p.keywords LIKE '%search_keyword%'\n  UNION ALL\n  SELECT \n    p.pubid,\n    p.keywords,\n    'paper' AS type,\n    p.paper_specific_column\n  FROM \n    papers p\n    INNER JOIN publications p ON p.pubid = p.pubid\n  WHERE \n    p.keywords LIKE '%search_keyword%'\n) AS combined_results;