DECLARE @image VARBINARY(MAX) = (SELECT * FROM OPENROWSET(BULK 'C:\path\to\image.jpg', SINGLE_BLOB) AS img)\nDECLARE @base64 NVARCHAR(MAX) = CONVERT(NVARCHAR(MAX), @image, 2)\n\nSET @emailHTML = '<html><body><img src="data:image/jpeg;base64,' + @base64 + '"></body></html>'\n\nEXEC msdb.dbo.sp_send_dbmail\n  @recipients = @p_recipients,\n  @subject = @v_subject,\n  @body = @emailHTML,\n  @body_format = 'HTML';