SELECT -- some kind of information\n       d1_d2_diff.BusinessDays AS d1_d2_diff,\n       d1_d3_diff.BusinessDays AS d1_d3_diff,\n       d2_d3_diff.BusinessDays AS d2_d3_diff,\n       d2_d4_diff.BusinessDays AS d2_d4_diff\nFROM some_table\nCROSS APPLY DateDiffBusinessDays(DateOne, DateTwo) AS d1_d2_diff\nCROSS APPLY DateDiffBusinessDays(DateOne, DateThree) AS d1_d3_diff\nCROSS APPLY DateDiffBusinessDays(DateTwo, DateThree) AS d2_d3_diff\nCROSS APPLY DateDiffBusinessDays(DateTwo, DateFour) AS d2_d4_diff;