CREATE TABLE publications (\n  pubid INT PRIMARY KEY,\n  keywords VARCHAR(255)\n);\n\nCREATE TABLE publication_types (\n  pubid INT,\n  type VARCHAR(20),\n  FOREIGN KEY (pubid) REFERENCES publications(pubid)\n);\n\nCREATE TABLE articles (\n  pubid INT,\n  article_specific_column VARCHAR(255),\n  FOREIGN KEY (pubid) REFERENCES publications(pubid)\n);\n\nCREATE TABLE books (\n  pubid INT,\n  book_specific_column VARCHAR(255),\n  FOREIGN KEY (pubid) REFERENCES publications(pubid)\n);\n\nCREATE TABLE papers (\n  pubid INT,\n  paper_specific_column VARCHAR(255),\n  FOREIGN KEY (pubid) REFERENCES publications(pubid)\n);