-- Create a symmetric key\nCREATE SYMMETRIC KEY MySymmetricKey\nWITH ALGORITHM = AES_256\nENCRYPTION BY PASSWORD = 'strong_password';\n\n-- Encrypt data\nDECLARE @plaintext nvarchar(128) = 'Sensitive Data';\nDECLARE @ciphertext varbinary(256);\nSET @ciphertext = ENCRYPTBYKEY(KEY_GUID('MySymmetricKey'), CONVERT(varbinary(128), @plaintext));\n\n-- Decrypt data\nDECLARE @decrypted_text nvarchar(128);\nSET @decrypted_text = DECRYPTBYKEY(KEY_GUID('MySymmetricKey'), @ciphertext);