SELECT Item.Id\nFROM Item\nJOIN Event\nON Event.Id = Item.EventId\nJOIN Seat\nUSING (VenueId, Configuration)\nWHERE Seat.Section = Item.Section\nAND Event.Home_team = 1\nAND Seat.exclude = 0\n\nUNION ALL\n\nSELECT Item.Id\nFROM Item\nJOIN Event\nON Event.Id = Item.EventId\nJOIN Seat\nUSING (VenueId, Configuration)\nWHERE Seat.Section = Item.Section\nAND Event.Home_team = 2\nAND Seat.exclude = 0\n\nUNION ALL\n\n...