CREATE TABLE MyTable (\n    Section1 CHAR(3),\n    Section2 CHAR(3),\n    Section3 INT,\n    CompositeKey AS (Section1 + '-' + Section2 + '-' + CONVERT(VARCHAR(4), Section3))\n);\n\n-- Insert sample data\nINSERT INTO MyTable (Section1, Section2, Section3)\nVALUES ('a00', '014', 2008);\n\n-- Create a view to display the composite key\nCREATE VIEW MyView AS\nSELECT CompositeKey, Section1, Section2, Section3\nFROM MyTable;\n\n-- Query the view\nSELECT * FROM MyView;