CREATE TABLE Calendar (\n  Date DATE PRIMARY KEY\n);\n\n-- populate the calendar table with dates\nINSERT INTO Calendar (Date)\nVALUES ('2008-12-10'), ('2008-12-11'), ..., ('2008-12-16');\n\nSELECT c.Date, COALESCE(l.Count, 0) AS Count\nFROM Calendar c\nLEFT JOIN (\n  SELECT DATE(login_date) AS Date, COUNT(*) AS Count\n  FROM WebsiteLogin\n  GROUP BY DATE(login_date)\n) l ON c.Date = l.Date;