IF EXISTS (SELECT name FROM sys.columns \n             WHERE name = 'COLUMN_THAT_NO_LONGER_EXISTS' AND object_id = OBJECT_ID('TABLE1'))\nBEGIN\n    DECLARE @sql nvarchar(max) = N'\n        INSERT INTO TABLE1\n        (\n            COLUMN_THAT_NO_LONGER_EXISTS,\n            COLUMN_B,\n            COLUMN_C\n        )\n        SELECT 1, 2, 3 FROM TABLE2';\n\n    EXEC sp_executesql @sql;\n\n    ALTER TABLE TABLE1 DROP COLUMN COLUMN_THAT_NO_LONGER_EXISTS;\nEND