-- Create sample tables\nCREATE TABLE Orders (\n    OrderID INT PRIMARY KEY,\n    CustomerID INT\n);\n\nCREATE TABLE Customers (\n    CustomerID INT PRIMARY KEY,\n    Name VARCHAR(50)\n);\n\n-- Insert sample data\nINSERT INTO Orders (OrderID, CustomerID) VALUES (1, 1), (2, 1), (3, 2), (4, 3);\nINSERT INTO Customers (CustomerID, Name) VALUES (1, 'John'), (2, 'Jane'), (3, 'Bob');\n\n-- Using IN\nSELECT * FROM Orders WHERE CustomerID IN (SELECT CustomerID FROM Customers WHERE Name = 'John');\n\n-- Using JOIN\nSELECT o.* FROM Orders o INNER JOIN Customers c ON o.CustomerID = c.CustomerID WHERE c.Name = 'John';