MERGE INTO resource_shortstrings AS target\nUSING (\n  SELECT \n    ue.prodtree_element_name_l_rk,\n    rs.resource_value\n  FROM \n    unique_elements ue\n    JOIN resource_shortstrings rs\n      ON ue.prodtree_element_name_l_rk = rs.resource_key\n    WHERE \n      ue.row_num = 1\n      AND rs.language_id = '5'\n) AS source\nON target.resource_key = source.prodtree_element_name_l_rk\nWHEN MATCHED THEN\n  UPDATE SET target.resource_value = source.resource_value\nWHEN NOT MATCHED THEN\n  INSERT (resource_key, resource_value, language_id)\n  VALUES (source.prodtree_element_name_l_rk, source.resource_value, '5');