SELECT \n  TRUNC(:start_date + (LEVEL - 1) / 24 / 60, 'MI') AS time_slice,\n  SUM(CASE WHEN t.start_time <= :start_date + (LEVEL - 1) / 24 / 60 \n              AND t.end_time >= :start_date + (LEVEL - 1) / 24 / 60 \n           THEN 1 ELSE 0 END) AS usage_count\nFROM \n  your_table t\nWHERE \n  t.start_time >= :start_date\n  AND t.end_time <= :end_date\n  AND t.owner = 'Alice'\nCONNECT BY LEVEL <= 24 * 60;