CREATE OR REPLACE PROCEDURE dynamic_query AS\n  TYPE cur_type IS REF CURSOR;\n  v_cur cur_type;\n  v_sql VARCHAR2(1000);\n  v_table_name VARCHAR2(30);\n  v_column_name VARCHAR2(30);\n  v_value VARCHAR2(100);\nBEGIN\n  FOR rec IN (SELECT table_name, column_name, numerator FROM MainTable) LOOP\n    v_table_name := rec.table_name;\n    v_column_name := rec.column_name;\n    v_sql := 'SELECT ' || v_column_name || ' FROM ' || v_table_name || ' WHERE numerator = :1';\n    OPEN v_cur FOR v_sql USING rec.numerator;\n    LOOP\n      FETCH v_cur INTO v_value;\n      EXIT WHEN v_cur%NOTFOUND;\n      DBMS_OUTPUT.PUT_LINE(v_value); -- Do something with the value\n    END LOOP;\n    CLOSE v_cur;\n  END LOOP;\nEND;\n/