WITH \n  intervals AS (\n    SELECT LEVEL - 1 AS minute_offset\n    FROM DUAL\n    CONNECT BY LEVEL <= 24 * 60  -- 1-minute intervals for 24 hours\n  ),\n  sliced_data AS (\n    SELECT \n      i.minute_offset,\n      SUM(CASE WHEN t.start_time + i.minute_offset <= t.end_time THEN 1 ELSE 0 END) AS usage_count\n    FROM \n      your_table t,\n      intervals i\n    WHERE \n      t.start_time >= :start_date  -- filter by start date\n      AND t.end_time <= :end_date  -- filter by end date\n      AND t.owner = 'Alice'  -- filter by owner\n    GROUP BY i.minute_offset\n  )\nSELECT \n  s.minute_offset AS time_slice,\n  s.usage_count\nFROM \n  sliced_data s\nORDER BY \n  s.minute_offset;