-- Rename table\nEXEC sp_rename 'tblFoo', 'tblFooBar';\n\n-- Rename primary key constraint\nDECLARE @sql nvarchar(max);\nSELECT @sql = 'EXEC sp_rename ''' + name + ''', ''PK_tblFooBar'''\nFROM sys.key_constraints\nWHERE parent_object_id = OBJECT_ID('tblFooBar') AND type = 'PK';\n\nEXEC sp_executesql @sql;\n\n-- Rename foreign key constraints\nSELECT @sql = COALESCE(@sql, '') + '\n    EXEC sp_rename ''' + name + ''', ''FK_' + OBJECT_NAME(parent_object_id) + '_tblFooBar''\n'\nFROM sys.foreign_keys\nWHERE referenced_object_id = OBJECT_ID('tblFooBar');\n\nEXEC sp_executesql @sql;